import React from 'react'
import Tarefas from './Tarefas'
import ButtonAddTarefa from './ButtonAddTarefa'

let arr = ['Orar', 'Jejuar', 'Ler Bíblia']

const TodoList = () => {
  return (
    <>
    <ButtonAddTarefa />
    {arr.map((item, index) => {
    return (
      <Tarefas item={item} index={index} />
    )
  })}}
    </>
  )
