import React from "react";
import Tarefas from "./Tarefas";



const ButtonAddTarefa = () =>{
  function addTarefa(){
    let novaTarefa = prompt("Digite a Tarefa:")
  
    return(
        <Tarefas objetivo={novaTarefa}/>
    )
  } 
  return(
    <div>
      <button onClick={addTarefa}>Add</button>
    </div>
  )
}

export default ButtonAddTarefa;
