import React from 'react'
import Tarefas from './Tarefas'
import ButtonAddTarefa from './ButtonAddTarefa'

let arr = ['Orar', 'Jejuar', 'Ler Bíblia']

const showList = ()=>{
  return
    {
      arr.map((item, index) => {
        return (<Tarefas item={item} index={index} />)
        })
    }
  
}

const TodoList = () => {
  return (
    <>
      {showList}
      <ButtonAddTarefa objetivos={arr}/>
    </>
  )
  /*
  return arr.map((item, index) => {
    return (
      <Tarefas item={item} index={index} />
    )
  })}*/
}

export default TodoList
