import React from 'react'

let arr = ['Orar', 'Jejuar', 'Ler Bíblia']

const Tarefas = () => {
  return arr.map((item, index) => {
    return <li key={index}>{item}</li>
  })

  const ButtonAddTarefa = () => {
    return <button onClick={novaTarefa}>Add</button>
  }
}

const novaTarefa = () => {
  let tarefa = prompt('Digite a nova Tarefa:')
}
