import React from 'react'

const Tarefas = props => {

  const excluir = (alvo)=>{
    document.querySelector(`#element${alvo}`).style = "text-decoration: line-through;"
  }

  return (
    <>
      <li onClick={()=> excluir(props.index)} key={props.index} id={`element${props.index}`}>
        {props.item}
      </li>
    </>
  )
}

export default Tarefas
