import React, { useState } from 'react'
import Tarefas from './Tarefas'
import objetivos from './objetivos.js'

function guardarTarefasPadrao(){
  localStorage.setItem("tarefas", objetivos)
}

function buscarTarefas(){
  let objetivosAtualizados = new Array(localStorage.getItem("tarefas"))
}

function atualizarTarefas(){
  localStorage.setItem("tarefas", listaAtualizada)
}



const TodoList = () => {
  const [arr, setArr] = useState(objetivos)


  function mostrarLista(){
    guardarTarefasPadrao()
    return objetivos.map((item, index) => {
      return <Tarefas item={item} index={index} />
    })
  }

  const addTarefa = ()=> {
    let novaTarefa = prompt('Digite a nova Tarefa:')
    objetivos.push(novaTarefa)
    guardarTarefas(objetivos)
    return setArr(mostrarLista)
  }
  
  return (
    <>
      <ul>{mostrarLista()}</ul>
      <button onClick={addTarefa} className='btn btn-success'>Nova Tarefa</button>
    </>
  )
}

export default TodoList
