import React from "react"

const ButtonAddTarefa = (props) => {
  const nova = () =>{
    let novo = props.test
    let novaTarefa = prompt('Digite a nova Tarefa:')
    novo.push(novaTarefa)
    console.log()
  }

  return <button onClick={nova}>Add</button>
}

export default ButtonAddTarefa
