import React from "react";
import Tarefas from "./Tarefas";

const novaTarefa = ()=>{
  let tarefa = prompt('Digite a nova Tarefa:')
}

const ButtonAddTarefa = () =>{

  return(
      <button onClick={novaTarefa}>Add</button>
  )
}

export default ButtonAddTarefa;
