import React from 'react'
const progresso = []
let classesAtuais = JSON.parse(localStorage.getItem('progresso'))

const concluirTarefa = alvo => {
    let elemento = document.querySelector(`#element${alvo}`)
    elemento.classList.toggle('tarefaConcluida')
    if (elemento.className === 'tarefaInacabada undefined') {
        classesAtuais[alvo] = 'tarefaInacabada'
        localStorage.setItem('progresso', JSON.stringify(classesAtuais))
        classesAtuais = JSON.parse(localStorage.getItem('progresso'))
        classesAtuais.forEach(classe => {
            if(classe === null){
                classe = "tarefaInacabada"
            }
        });
        localStorage.setItem('progresso', JSON.stringify(classesAtuais))
        
      }
    if (elemento.className === 'tarefaInacabada undefined tarefaConcluida') {
        classesAtuais[alvo] = 'tarefaConcluida'
        localStorage.setItem('progresso', JSON.stringify(classesAtuais))
        let classesAtuais = JSON.parse(localStorage.getItem('progresso'))
        classesAtuais.forEach(classe => {
            if(classe === null){
                classe = "tarefaInacabada"
            }
        });
        localStorage.setItem('progresso', JSON.stringify(classesAtuais))
    
      }
}

const Tarefas = props => {
    return (
        <>
            <li key={props.index}>
                <button
                    onClick={() => concluirTarefa(props.index)}
                    id={`element${props.index}`}
                    className={`tarefaInacabada ${props.classe}`}
                >
                    {props.item}
                </button>
            </li>
        </>
    )
}

export default Tarefas
